@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {

}
<div class="skills">
    <h2>My Skills.</h2>
    <div class="skill-row">
        <img class="mskill" src="https://media.giphy.com/media/RbDKaczqWovIugyJmW/giphy.gif" alt="coding gif">
        <h3>Design & Development</h3>
        <p class="skill-description"> I have good knowledge of HTML-5 , CSS-3, JvaScript, Angular Framework. <br> Capable of
            Documentation and Deployment on live site.<br>Excellent self-starter & Quick learner
        of new concepts, and technology &open to any challenges at workplace. </p>
    </div>

</div>
